mapper.select:
select count(*) from PRODUCT p join GOLFCC g on p.CC_ID = g.CC_ID join MEMBERLIST m on p.USER_NO = m.USER_NO JOIN LOCATION l on l.LOC_ID = g.LOC_ID where '1'='1'
and g.loc_id = #{loc_id}
and ${productDivision} like '%'||#{productValue}||'%'

select p_id,p_pdate ,p_uploaddate ,p_maxpeople,p_hole,p_caddyyn,p_babyn,p_cartyn,p_price,p_content,user_no,cc_id,p_vcount
from  PRODUCT
where p_id = #{value}
